import React, { Component } from "react";
// import "./style.css";

class Main extends Component {
  // Setting the component's initial state
  state = {
    score: "",
    topScore: ""
  };

  images = [
    {id:1, src:"../../../public/assets/images/ironman.png"},
    {id:2, src:"../../../public/assets/images/blackpanther.png"},
    {id:3, src:"../../../public/assets/images/blackwidow.png"},
    {id:4, src:"../../../public/assets/images/captain.png"},
    {id:5, src:"../../../public/assets/images/drstrange.png"},
    {id:6, src:"../../../public/assets/images/falcon.png"},
    {id:7, src:"../../../public/assets/images/hawk.png"},
    {id:8, src:"../../../public/assets/images/hulk.png"},
    {id:9, src:"../../../public/assets/images/vision.png"},
    {id:10, src:"../../../public/assets/images/peter.png"},
    {id:11, src:"../../../public/assets/images/spiderman.png"},
    {id:12, src:"../../../public/assets/images/thor.png"},
  ];

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = event.target;

    // Updating the input's state
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();

    // Alert the user their first and last name, clear `this.state.firstName` and `this.state.lastName`, clearing the inputs
    alert(`Hello ${this.state.firstName} ${this.state.lastName}`);
    this.setState({
      firstName: "",
      lastName: ""
    });
  };

  render() {
    // Notice how each input has a `value`, `name`, and `onChange` prop
    return (
        this.images.map(
      <div>
        <p>
          Hello {this.state.firstName} {this.state.lastName}
        </p>

        <form className="form">
          <input
            value={this.state.firstName}
            name="firstName"
            onChange={this.handleInputChange}
            type="text"
            placeholder="First Name"
          />
          <input
            value={this.state.lastName}
            name="lastName"
            onChange={this.handleInputChange}
            type="text"
            placeholder="Last Name"
          />
          <button onClick={this.handleFormSubmit}>Submit</button>
        </form>
      </div>
    )
    )
  }
}

export default Main;
